\r\n.ngfb-buttons img:hover {\r\n	border:none;\r\n}\r\n.ngfb-buttons iframe {\r\n	margin:0 !important;\r\n	max-width:none;\r\n	vertical-align:baseline;\r\n}\r\n.ngfb-buttons > div,		/* default uses small buttons */\r\n.ngfb-preset-small_share_count .ngfb-buttons > div {\r\n	display:inline-block;\r\n	vertical-align:bottom;\r\n	text-align:left;\r\n	line-height:20px;\r\n	padding:0;\r\n	margin:5px;\r\n	width:100px;\r\n	height:20px;\r\n}\r\n.ngfb-preset-large_share_vertical .ngfb-buttons > div,\r\n.ngfb-preset-large_share_vertical .ngfb-buttons > div[class*="-button"] {\r\n	line-height:61px;\r\n	width:70px;\r\n	height:61px;\r\n}\r\n\r\n/* Facebook Button */\r\n\r\n.ngfb-buttons div.facebook-button,		/* English (Default) */\r\n.ngfb-preset-small_share_count .ngfb-buttons div.facebook-button {\r\n	width:114px;\r\n}\r\n.ngfb-buttons.cs_CZ div.facebook-button,	/* Czech */\r\n.ngfb-preset-small_share_count .ngfb-buttons.cs_CZ div.facebook-button {\r\n	width:144px;\r\n}\r\n.ngfb-buttons div.fb-share-button,\r\n.ngfb-preset-small_share_count .ngfb-buttons div.fb-share-button {\r\n	width:90px;\r\n}\r\n.ngfb-preset-large_share_vertical .ngfb-buttons div.fb-share-button {\r\n	height:40px;\r\n	line-height:40px;\r\n}\r\n.ngfb-buttons div.fb-share-button span {\r\n	display:inline-block	!important;\r\n}\r\n\r\n/* Google+ Button */\r\n\r\n.ngfb-buttons div.gplus-button,\r\n.ngfb-preset-small_share_count .ngfb-buttons div.gplus-button {\r\n	width:99px;\r\n	vertical-align:top;\r\n}\r\n.ngfb-buttons div.gplusone-button,\r\n.ngfb-preset-small_share_count .ngfb-buttons div.gplusone-button {\r\n	width:75px;\r\n	line-height:10px;\r\n	vertical-align:top;\r\n}\r\n\r\n/* Twitter Button */\r\n\r\n.ngfb-buttons div.twitter-button,\r\n.ngfb-preset-small_share_count .ngfb-buttons div.twitter-button,\r\n.ngfb-preset-large_share_vertical .ngfb-buttons div.twitter-button {\r\n	width:61px;\r\n	height:20px;\r\n	line-height:20px;\r\n	vertical-align:top;\r\n}\r\n\r\n/* LinkedIn Button */\r\n\r\n.ngfb-buttons div.linkedin-button,\r\n.ngfb-preset-small_share_count .ngfb-buttons div.linkedin-button {\r\n	width:110px;\r\n}\r\n\r\n/* Pinterest Button */\r\n\r\n.ngfb-buttons div.pinterest-button,\r\n.ngfb-preset-small_share_count .ngfb-buttons div.pinterest-button {\r\n	width:85px;\r\n}\r\n.ngfb-preset-large_share_vertical .ngfb-buttons div.pinterest-button {\r\n	height:40px;\r\n	margin-top:35px;\r\n}\r\n\r\n/* Reddit Button */\r\n\r\n.ngfb-buttons div.reddit-button,\r\n.ngfb-preset-small_share_count .ngfb-buttons div.reddit-button {\r\n	width:130px;\r\n}\r\n.ngfb-preset-large_share_vertical .ngfb-buttons div.reddit-button {\r\n	margin-bottom:10px;\r\n}\r\n\r\n/* StumbleUpon Button */\r\n\r\n.ngfb-buttons div.stumbleupon-button,\r\n.ngfb-preset-small_share_count .ngfb-buttons div.stumbleupon-button {\r\n	width:84px;\r\n}\r\n.ngfb-preset-large_share_vertical .ngfb-buttons div.stumbleupon-button {\r\n	height:60px;\r\n	line-height:60px;\r\n}\r\n\r\n/* Tumblr Button */\r\n\r\n.ngfb-buttons div.tumblr-button,\r\n.ngfb-preset-small_share_count .ngfb-buttons div.tumblr-button {\r\n	width:85px;\r\n}\r\n.ngfb-preset-large_share_vertical .ngfb-buttons div.tumblr-button {\r\n	height:40px;\r\n}\r\n\r\n/*\r\n * Non-JavaScript Buttons\r\n */\r\n\r\n.ngfb-buttons div.css-button a {\r\n	display:inline-block;\r\n	line-height:20px;\r\n	width:60px;\r\n	font-size:10px;\r\n	border-radius:3px;\r\n	text-align:center;\r\n	text-decoration:none;\r\n	vertical-align:top;\r\n	border:0.5px solid #ddd;\r\n	box-shadow:none;\r\n	-webkit-box-shadow:none;\r\n	-webkit-font-smoothing:antialiased;\r\n	-moz-osx-font-smoothing:grayscale;\r\n}\r\n\r\n.ngfb-buttons div.css-button a .ngfb-icon {\r\n	fill:#ffffff;\r\n	vertical-align:bottom;\r\n}\r\n\r\n.ngfb-buttons div.css-button a .ngfb-text {\r\n	color:#ffffff;\r\n	white-space:nowrap;\r\n	font-weight:bold;\r\n}\r\n\r\n/* E-Mail Button */\r\n\r\n.ngfb-buttons div.email-button,\r\n.ngfb-preset-small_share_count .ngfb-buttons div.email-button {\r\n	width:62px;\r\n}\